
const PRODUCTS = [
  {id:'p1', title:'Labial líquido mate Ruby', category:'labial', price:25900, img:'https://encrypted-tbn0.gstatic.com/shopping?q=tbn:ANd9GcTA85nHjS3Moe-QgstJ8OC-kL_8ggEpmdYizCd65FvChDGpWrleZBXGLBdmMW95ygP4CmWmAAWY0U1V6AsEF-FnzqkrCD2BQLjA32vAVTzW9NXcAE1AClhQ'},
  {id:'p2', title:'Base fluida Glow 30ml', category:'base', price:45900, img:'data:image/jpeg;base64,'},
  {id:'p3', title:'Rímel Volumen Extra', category:'pestañas', price:19900, img:'data:image/png;base64,'},
  {id:'p4', title:'Paleta sombras Sunset', category:'pestañas', price:32900, img:'data:image/jpeg;base64,'},
  {id:'p5', title:'Iluminador en polvo Star', category:'base', price:16900, img:'data:image/jpeg;base64,'},
  {id:'p6', title:'Labial crema Velvet', category:'labial', price:18900, img:'data:image/jpeg;base64,'}
];

let CART = {};
const STORAGE_KEY = 'belleza_carrito_v1';
const REVIEWS_KEY = 'belleza_reviews_v1';
const USERS_KEY = 'belleza_users_v1';
const MESSAGES_KEY = 'belleza_messages_v1';

const currency = (n) => n.toLocaleString('es-CO', {style:'currency',currency:'COP',maximumFractionDigits:0});


function loadState(){
  const raw = localStorage.getItem(STORAGE_KEY);
  if(raw){ try{ CART = JSON.parse(raw); }catch(e){ CART = {}; } }
  else CART = {};
}
function saveState(){ localStorage.setItem(STORAGE_KEY, JSON.stringify(CART)); }

function loadUsers(){
  const raw = localStorage.getItem(USERS_KEY);
  if(!raw) return [];
  try{ return JSON.parse(raw); }catch(e){ return []; }
}
function saveUsers(users){ localStorage.setItem(USERS_KEY, JSON.stringify(users)); }

function loadReviews(){
  const raw = localStorage.getItem(REVIEWS_KEY);
  if(!raw) return [{name:'Valeria', rating:5, text:'Excelente pigmenación, llega rápido.'}];
  try { return JSON.parse(raw); } catch(e){ return []; }
}
function saveReviews(reviews){ localStorage.setItem(REVIEWS_KEY, JSON.stringify(reviews)); }

function loadMessages(){
  const raw = localStorage.getItem(MESSAGES_KEY);
  if(!raw) return [];
  try { return JSON.parse(raw); } catch(e){ return []; }
}
function saveMessages(msgs){ localStorage.setItem(MESSAGES_KEY, JSON.stringify(msgs)); }

  
const productGrid = document.getElementById('productGrid');
const filter = document.getElementById('filter');
const search = document.getElementById('search');

function renderProducts(filterVal = 'all', q=''){
  productGrid.innerHTML = '';
  const normalized = q.trim().toLowerCase();
  const list = PRODUCTS.filter(p => (filterVal==='all' || p.category===filterVal)
    && (p.title.toLowerCase().includes(normalized) || p.category.includes(normalized)));
  if(list.length===0){
    productGrid.innerHTML = '<div class="panel" style="grid-column:1/-1">No se encontraron productos.</div>';
    return;
  }
  list.forEach(p=>{
    const el = document.createElement('div'); el.className='card';
    el.innerHTML = `
      <div class="thumb"><img src="${p.img}" alt="${p.title}"></div>
      <div class="meta">
        <h4>${p.title}</h4>
        <div class="price">${currency(p.price)}</div>
        <div class="desc">Categoría: <b>${p.category}</b></div>
      </div>
      <div class="card-actions">
        <button class="small" data-id="${p.id}" title="Ver">Ver</button>
        <button class="small btn addBtn" data-id="${p.id}">Añadir</button>
      </div>
    `;
    productGrid.appendChild(el);
  });
}


const cartList = document.getElementById('cartList');
const cartCount = document.getElementById('cartCount');
const subtotalEl = document.getElementById('subtotal');
const shippingEl = document.getElementById('shipping');
const totalEl = document.getElementById('total');
const cartEmpty = document.getElementById('cartEmpty');
const deliveryOption = document.getElementById('deliveryOption');
const deliveryETA = document.getElementById('deliveryETA');
const userStatus = document.getElementById('userStatus');

function addToCart(productId, qty = 1){
  const product = PRODUCTS.find(p=>p.id===productId);
  if(!product) return;
  if(CART[productId]) CART[productId].qty += qty;
  else CART[productId] = {product, qty};
  saveState(); renderCart(); flashCartCount();
}
function removeFromCart(productId){ delete CART[productId]; saveState(); renderCart(); }
function updateQty(productId, newQty){ if(newQty<=0){ removeFromCart(productId); return; } if(CART[productId]){ CART[productId].qty = newQty; saveState(); renderCart(); } }
function clearCart(){ CART = {}; saveState(); renderCart(); }
function cartTotals(){
  let sub = 0;
  Object.values(CART).forEach(i => sub += i.product.price * i.qty);
  let ship = 0;
  const option = deliveryOption.value;
  if(option === 'home') ship = 5000;
  if(option === 'express') ship = 10000;
  if(option === 'pickup') ship = 0;
  if(sub >= 120000) ship = 0;
  return {sub, ship, total: sub + ship};
}
function renderCart(){
  cartList.innerHTML = '';
  const items = Object.values(CART);
  if(items.length===0){ cartEmpty.style.display = 'block'; cartCount.textContent = '0'; }
  else {
    cartEmpty.style.display = 'none';
    items.forEach(i=>{
      const row = document.createElement('div'); row.className='cart-item';
      row.innerHTML = `
        <div class="ci-thumb"><img src="${i.product.img}" alt="${i.product.title}" style="max-width:84%;max-height:84%;border-radius:8px"></div>
        <div class="ci-meta">
          <b>${i.product.title}</b>
          <div class="muted">${currency(i.product.price)} • ${i.qty} unidad(es)</div>
        </div>
        <div style="display:flex;flex-direction:column;gap:6px;align-items:flex-end">
          <div class="qty">
            <button class="dec" data-id="${i.product.id}">−</button>
            <div style="min-width:28px;text-align:center">${i.qty}</div>
            <button class="inc" data-id="${i.product.id}">+</button>
          </div>
          <button class="small outline remove" data-id="${i.product.id}">Eliminar</button>
        </div>
      `;
      cartList.appendChild(row);
    });
    cartCount.textContent = items.reduce((s,i)=>s+i.qty,0);
  }
  const t = cartTotals();
  subtotalEl.textContent = currency(t.sub);
  shippingEl.textContent = currency(t.ship);
  totalEl.textContent = currency(t.total);
  if(t.total === 0) deliveryETA.textContent = '--';
  else {
    if(deliveryOption.value === 'express') deliveryETA.textContent = 'Llegan en 30-60 min';
    else if(deliveryOption.value === 'home') deliveryETA.textContent = 'Llegan en 2-4 hrs';
    else deliveryETA.textContent = 'Listo para recoger';
  }

  cartList.querySelectorAll('.inc').forEach(b => b.onclick = () => { updateQty(b.dataset.id, CART[b.dataset.id].qty + 1); });
  cartList.querySelectorAll('.dec').forEach(b => b.onclick = () => { updateQty(b.dataset.id, CART[b.dataset.id].qty - 1); });
  cartList.querySelectorAll('.remove').forEach(b => b.onclick = () => { removeFromCart(b.dataset.id); });
}


function renderReviews(){
  const reviews = loadReviews();
  const list = document.getElementById('reviewsList'); list.innerHTML = '';
  reviews.slice().reverse().forEach(r=>{
    const el = document.createElement('div'); el.style.display='flex'; el.style.flexDirection='column';
    el.innerHTML = `<div style="display:flex;justify-content:space-between;align-items:center">
      <div><b>${r.name}</b> <span class="muted" style="font-size:13px">• ${new Date(r.date||Date.now()).toLocaleDateString()}</span></div>
      <div class="stars">${'★'.repeat(r.rating)}${'☆'.repeat(5-r.rating)}</div>
    </div>
    <div class="muted" style="margin-top:6px">${r.text}</div>`;
    list.appendChild(el);
  });
}


const contactForm = document.getElementById('contactForm');
const contactFeedback = document.getElementById('contactFeedback');
contactForm.addEventListener('submit', (ev)=>{
  ev.preventDefault();
  const name = document.getElementById('contactName').value.trim();
  const email = document.getElementById('contactEmail').value.trim();
  const phone = document.getElementById('contactPhone').value.trim();
  const topic = document.getElementById('contactTopic').value;
  const message = document.getElementById('contactMessage').value.trim();
  if(!name || !email || !message) return showContactFeedback('Completa los campos obligatorios', true);
  const msgs = loadMessages();
  msgs.push({name,email,phone,topic,message,date:Date.now()});
  saveMessages(msgs);
  contactForm.reset();
  showContactFeedback('Mensaje enviado. Te responderemos pronto.');
});
document.getElementById('contactClear').addEventListener('click', ()=>{ contactForm.reset(); contactFeedback.style.display='none'; });
function showContactFeedback(txt, isError=false){ contactFeedback.style.display='block'; contactFeedback.textContent = txt; contactFeedback.style.color = isError ? 'crimson' : 'green'; }


const modal = document.getElementById('modal');
const authBtn = document.getElementById('authBtn');
const closeModal = document.getElementById('closeModal');
const tabLogin = document.getElementById('tabLogin');
const tabRegister = document.getElementById('tabRegister');
const loginPanel = document.getElementById('loginPanel');
const registerPanel = document.getElementById('registerPanel');
const loginForm = document.getElementById('loginForm');
const registerForm = document.getElementById('registerForm');
const loginFeedback = document.getElementById('loginFeedback');
const regFeedback = document.getElementById('regFeedback');
const logoutBtn = document.getElementById('logoutBtn');
const userStatusEl = document.getElementById('userStatus');

function openModal(){ modal.style.display = 'flex'; modal.setAttribute('aria-hidden','false'); }
function closeAuth(){ modal.style.display = 'none'; modal.setAttribute('aria-hidden','true'); }

authBtn.addEventListener('click', openModal);
closeModal.addEventListener('click', closeAuth);
modal.addEventListener('click', (e)=>{ if(e.target === modal) closeAuth(); });

tabLogin.addEventListener('click', ()=>{ tabLogin.classList.add('active'); tabRegister.classList.remove('active'); loginPanel.style.display='block'; registerPanel.style.display='none'; });
tabRegister.addEventListener('click', ()=>{ tabRegister.classList.add('active'); tabLogin.classList.remove('active'); registerPanel.style.display='block'; loginPanel.style.display='none'; });

function setLoggedUser(user){
  sessionStorage.setItem('belleza_session', JSON.stringify(user));
  updateUserUI();
}
function clearLoggedUser(){
  sessionStorage.removeItem('belleza_session');
  updateUserUI();
}
function getLoggedUser(){
  const raw = sessionStorage.getItem('belleza_session');
  if(!raw) return null;
  try{ return JSON.parse(raw); }catch(e){ return null; }
}
function updateUserUI(){
  const u = getLoggedUser();
  if(u){ userStatusEl.textContent = `Sesión: ${u.name} (${u.email})`; logoutBtn.style.display = 'inline-block'; }
  else { userStatusEl.textContent = 'No has iniciado sesión'; logoutBtn.style.display = 'none'; }
}


registerForm.addEventListener('submit', (ev)=>{
  ev.preventDefault();
  const name = document.getElementById('regName').value.trim();
  const email = document.getElementById('regEmail').value.trim().toLowerCase();
  const pass = document.getElementById('regPassword').value;
  if(pass.length < 6) return showRegFeedback('La contraseña debe tener al menos 6 caracteres', true);
  const users = loadUsers();
  if(users.find(u=>u.email === email)) return showRegFeedback('Ya existe una cuenta con ese correo', true);
  users.push({name,email,password:pass});
  saveUsers(users);
  showRegFeedback('Cuenta creada con éxito', false);
  setTimeout(()=>{ setLoggedUser({name,email}); closeAuth(); }, 700);
});
function showRegFeedback(txt, isError=false){ regFeedback.style.display='block'; regFeedback.textContent = txt; regFeedback.style.color = isError ? 'crimson' : 'green'; }


loginForm.addEventListener('submit', (ev)=>{
  ev.preventDefault();
  const email = document.getElementById('loginEmail').value.trim().toLowerCase();
  const pass = document.getElementById('loginPassword').value;
  const users = loadUsers();
  const u = users.find(x=>x.email===email && x.password===pass);
  if(!u) return showLoginFeedback('Credenciales incorrectas', true);
  showLoginFeedback('Inicio de sesión correcto', false);
  setTimeout(()=>{ setLoggedUser({name:u.name,email:u.email}); closeAuth(); }, 600);
});
function showLoginFeedback(txt, isError=false){ loginFeedback.style.display='block'; loginFeedback.textContent = txt; loginFeedback.style.color = isError ? 'crimson' : 'green'; }

/* Logout */
logoutBtn.addEventListener('click', ()=>{
  if(confirm('¿Cerrar sesión?')){ clearLoggedUser(); showLoginFeedback('Sesión cerrada', false); }
});


document.addEventListener('click', (e)=>{
  if(e.target.matches('.addBtn')) addToCart(e.target.dataset.id, 1);
  if(e.target.matches('#openCart')) { document.querySelector('aside').scrollIntoView({behavior:'smooth'}); }
});

document.getElementById('clearCart').addEventListener('click', ()=>{
  if(confirm('¿Vaciar todo el carrito?')) clearCart();
});
document.getElementById('giftBtn').addEventListener('click', ()=>{
  const gift = {id:'gift', title:'Set de Regalo', category:'promo', price:0, img:'https://images.unsplash.com/photo-1607746882042-944635dfe10e?q=80&w=600&auto=format&fit=crop&ixlib=rb-4.0.3&s=7'};
  if(!PRODUCTS.find(p=>p.id==='gift')) PRODUCTS.push(gift);
  addToCart('gift',1);
});
deliveryOption.addEventListener('change', renderCart);

document.getElementById('checkoutBtn').addEventListener('click', ()=>{
  const t = cartTotals();
  if(t.total === 0){ alert('Tu carrito está vacío'); return; }
  const u = getLoggedUser();
  let address = '';
  if(deliveryOption.value !== 'pickup'){
    if(u) address = prompt(`Dirección de entrega (usuario: ${u.email})`,'');
    else address = prompt('Dirección de entrega (invitado)');
    if(address === null) return;
  }
  const method = deliveryOption.value;
  const confirmMsg = `Resumen:\nTotal: ${currency(t.total)}\nEnvío: ${currency(t.ship)}\nMétodo: ${method}\nDirección: ${address || '—'}\n\nConfirmar pedido?`;
  if(confirm(confirmMsg)){
    clearCart();
    alert('Gracias — Tu pedido ha sido recibido y se procesará en breve.');
  }
});


document.querySelectorAll('#menu a').forEach(a=>{
  a.addEventListener('click', (ev)=>{
    ev.preventDefault();
    document.querySelectorAll('#menu a').forEach(x=>x.classList.remove('active'));
    a.classList.add('active');
    const section = a.getAttribute('data-section');
    if(section === 'reseñas') document.getElementById('reseñas').scrollIntoView({behavior:'smooth'});
    else if(section === 'contacto') document.getElementById('contacto').scrollIntoView({behavior:'smooth'});
    else window.scrollTo({top:0, behavior:'smooth'});
  });
});


filter.addEventListener('change', ()=> renderProducts(filter.value, search.value));
search.addEventListener('input', ()=> renderProducts(filter.value, search.value));

document.getElementById('reviewForm').addEventListener('submit', (ev)=>{
  ev.preventDefault();
  const name = document.getElementById('reviewName').value.trim() || 'Anónimo';
  const rating = parseInt(document.getElementById('reviewRating').value,10);
  const text = prompt('Tu reseña (breve):');
  if(!text) return alert('Reseña cancelada');
  const reviews = loadReviews();
  reviews.push({name, rating, text, date: Date.now()});
  saveReviews(reviews); renderReviews();
  document.getElementById('reviewName').value = '';
});

/* Simple "Ver" modal: alert */
document.body.addEventListener('click', (e)=>{
  if(e.target.matches('.small') && e.target.textContent.trim()==='Ver'){
    const id = e.target.dataset.id;
    const p = PRODUCTS.find(x=>x.id===id);
    if(!p) return;
    const info = `${p.title}\n\nPrecio: ${currency(p.price)}\nCategoría: ${p.category}\n\nDescripción:\nMaquillaje de alta calidad. Envíos en Medellín.`;
    alert(info);
  }
});

/* Helpers */
function flashCartCount(){ cartCount.animate([{transform:'scale(1.05)'},{transform:'scale(1)'}],{duration:180}); }

/* Persistencia al cerrar */
window.addEventListener('beforeunload', saveState);

/* Init */
loadState();
renderProducts();
renderCart();
renderReviews();
updateUserUI();